pragma solidity ^0.5.0;

contract Decentragram {
    // Code goes here...
    string public name = "Decentragram";

    //Store Images
    uint256 public imageCount = 0;
    mapping(uint256 => Image) public images;
    struct Image {
        uint256 id;
        string hash;
        string description;
        uint256 tipAmount;
        address payable author;
    }
    event ImageCreated(
        uint256 id,
        string hash,
        string description,
        uint256 tipAmount,
        address payable author
    );
    event ImageTipped(
        uint256 id,
        string hash,
        string description,
        uint256 tipAmount,
        address payable author
    );

    //Create posts
    function uploadImage(string memory _imageHash, string memory _description)
        public
    {
        //Make sure the description hash exists
        require(bytes(_description).length > 0);

        //Make sure the image hash exists
        require(bytes(_imageHash).length > 0);

        //Make sure the image hash exists
        require(msg.sender != address(0x0));

        //Increment image id
        imageCount++;

        //Add Image to contract
        images[imageCount] = Image(
            imageCount,
            _imageHash,
            _description,
            0,
            msg.sender
        );
        //Trigger an event to show that the image has been created
        emit ImageCreated(imageCount, _imageHash, _description, 0, msg.sender);
    }

    //Tip images
    function tipImageOwner(uint256 _id) public payable {
        require(_id > 0 && _id <= imageCount);
        //Fetch the image
        Image memory _image = images[_id];
        //Fetch the author
        address payable _author = _image.author;
        //Pay the author by sending them Ether
        address(_author).transfer(msg.value);
        //Increment the tip amount
        _image.tipAmount = _image.tipAmount + msg.value;
        //Update the image
        images[_id] = _image;
        //Trigger an event to show that the image has been created
        emit ImageTipped(
            _id,
            _image.hash,
            _image.description,
            _image.tipAmount,
            _author
        );
    }
}
